import React from 'react';
import DeleteIcon from '../assest/image/delete-icon.png';
import OrangeCircleIcon from '../assest/image/orange-circle.png';

const TabItem = (props) => {
  const { isSelect, method, content, handleCalbackDeleteTab, handleCalbackActiveTab } = props;
  const [isHover, setIsHover] = React.useState(false);

  const onDeleteTab = () => {
    handleCalbackDeleteTab();
  }

  const onSelectTab = () => {
    handleCalbackActiveTab();
  }

  const handleHoverTab = (e) => {
    const { type } = e;
    console.log(type);
    setIsHover(e.type === 'mouseenter');
  }

  const contentFixed = (orginalContent) => {
    if (orginalContent.length > 12) {
      return orginalContent.slice(0, 12) + '...';
    }
    return orginalContent;
  }

  return (
    <div 
      className="tab-item-container"
      id="tab-item"
      onClick={() => onSelectTab()}
      //check hover tab
      onMouseEnter={(e) => handleHoverTab()}
      style={{backgroundColor: isSelect ? '#f5c7ae' : 'white'}}
    >
      <div className="method">{method}</div>
      <div className="content">
        {contentFixed(content)}
      </div>
      {isHover && (
      <div
        className="delete-icon"
        onClick={() => onDeleteTab()}
      >
        <img src={DeleteIcon} alt="delete-icon" />
      </div>
      )}
      {isSelect && (
        <div
        className="delete-icon"
        onClick={() => onDeleteTab()}
      >
        <img src={OrangeCircleIcon} alt="delete-icon" />
      </div>
      )}
    </div>
  );
}

export default TabItem;
